SELECT c.url FROM constructors c JOIN constructorResults cr ON c.constructorId = cr.constructorId WHERE c.nationality = 'Italian' GROUP BY c.constructorId, c.url ORDER BY SUM(cr.points) DESC LIMIT 1;
SELECT c.url FROM constructors c JOIN constructorStandings cs ON c.constructorId = cs.constructorId GROUP BY c.constructorId, c.url ORDER BY SUM(cs.wins) DESC LIMIT 1;
SELECT d.forename, d.surname FROM drivers d JOIN lapTimes l ON d.driverId = l.driverId JOIN races r ON l.raceId = r.raceId WHERE r.name = 'French Grand Prix' AND l.lap = 3 ORDER BY l.milliseconds DESC LIMIT 1;
SELECT raceId, MIN(milliseconds) AS fastest_lap_time_ms FROM lapTimes WHERE lap = 1 GROUP BY raceId ORDER BY fastest_lap_time_ms ASC LIMIT 1;
SELECT AVG(CAST(SUBSTR(r.fastestLapTime, 1, INSTR(r.fastestLapTime, ':') - 1) AS INTEGER) * 60000 + CAST(REPLACE(SUBSTR(r.fastestLapTime, INSTR(r.fastestLapTime, ':') + 1), '.', '') AS INTEGER)) / 1000.0 FROM results r JOIN races ra ON r.raceId = ra.raceId WHERE ra.name = 'United States Grand Prix'   AND ra.year = 2006   AND r.rank < 11;
SELECT d.forename, d.surname FROM drivers d JOIN pitStops p ON d.driverId = p.driverId WHERE d.nationality = 'German' AND d.dob BETWEEN '1980-01-01' AND '1985-12-31' GROUP BY d.driverId, d.forename, d.surname ORDER BY AVG(CAST(p.duration AS REAL)) ASC LIMIT 3;
SELECT d.forename, d.surname, r.time FROM results r JOIN drivers d ON r.driverId = d.driverId JOIN races ra ON r.raceId = ra.raceId WHERE ra.name = 'Canadian Grand Prix' AND ra.year = 2008 AND r.position = 1;
SELECT c.constructorRef, c.url FROM results r JOIN constructors c ON r.constructorId = c.constructorId JOIN races ra ON r.raceId = ra.raceId WHERE ra.name = 'Singapore Grand Prix' AND ra.year = 2009 AND r.position = 1;
SELECT forename, surname, dob FROM drivers WHERE nationality = 'Austrian'   AND strftime('%Y', dob) BETWEEN '1981' AND '1991';
SELECT forename || ' ' || surname AS full_name, url, dob FROM drivers WHERE nationality = 'German' AND dob BETWEEN '1971-01-01' AND '1985-12-31' ORDER BY dob DESC;
SELECT location, country, lat, lng FROM circuits WHERE name = 'Hungaroring';
SELECT cr.points, c.name, c.nationality FROM constructorResults cr JOIN races r ON cr.raceId = r.raceId JOIN constructors c ON cr.constructorId = c.constructorId WHERE r.name = 'Monaco Grand Prix' AND r.year BETWEEN 1980 AND 2010 ORDER BY cr.points DESC LIMIT 1;
SELECT AVG(r.points)  FROM results r JOIN drivers d ON r.driverId = d.driverId JOIN races ra ON r.raceId = ra.raceId WHERE d.forename = 'Lewis'    AND d.surname = 'Hamilton'   AND ra.name = 'Turkish Grand Prix';
SELECT AVG(race_count) AS average_races_per_year FROM (     SELECT year, COUNT(*) AS race_count     FROM races     WHERE year BETWEEN 2000 AND 2009     GROUP BY year );
SELECT nationality FROM drivers GROUP BY nationality ORDER BY COUNT(*) DESC LIMIT 1;
SELECT wins FROM driverStandings WHERE driverId IN (SELECT driverId FROM driverStandings GROUP BY driverId ORDER BY SUM(points) DESC LIMIT 1 OFFSET 90);
SELECT r.name FROM races r JOIN results res ON r.raceId = res.raceId WHERE res.fastestLapTime IS NOT NULL ORDER BY CAST(SUBSTR(res.fastestLapTime, 1, INSTR(res.fastestLapTime, ':') - 1) AS INTEGER) * 60000 +          CAST(SUBSTR(res.fastestLapTime, INSTR(res.fastestLapTime, ':') + 1, INSTR(res.fastestLapTime, '.') - INSTR(res.fastestLapTime, ':') - 1) AS INTEGER) * 1000 +          CAST(SUBSTR(res.fastestLapTime, INSTR(res.fastestLapTime, '.') + 1) AS INTEGER) LIMIT 1;
