SELECT d.forename, d.surname FROM qualifying q JOIN drivers d ON q.driverId = d.driverId JOIN races r ON q.raceId = r.raceId JOIN circuits c ON r.circuitId = c.circuitId WHERE r.year = 2008   AND c.name = 'Marina Bay Street Circuit'   AND q.position = 1   AND q.q3 IS NOT NULL ORDER BY q.q3 ASC LIMIT 1;
SELECT d.forename || ' ' || d.surname AS full_name, d.nationality, r.name AS first_race_name FROM drivers d JOIN (     SELECT res.driverId, MIN(r.date) AS first_race_date     FROM results res     JOIN races r ON res.raceId = r.raceId     GROUP BY res.driverId ) first_race ON d.driverId = first_race.driverId JOIN races r ON first_race.first_race_date = r.date WHERE d.dob = (SELECT MAX(dob) FROM drivers);
SELECT COUNT(*) AS accident_count FROM results r JOIN races ra ON r.raceId = ra.raceId JOIN status s ON r.statusId = s.statusId WHERE ra.name = 'Canadian Grand Prix' AND s.status = 'Accident' GROUP BY r.driverId ORDER BY accident_count DESC LIMIT 1;
