SELECT COUNT(*)  FROM Player_Attributes  WHERE crossing = (SELECT MAX(crossing) FROM Player_Attributes)    AND preferred_foot = 'left';
SELECT      (COUNT(CASE WHEN strength > 80 AND stamina > 80 THEN 1 END) * 100.0 / COUNT(*)) AS percentage FROM Player_Attributes;
SELECT c.name FROM Country c JOIN League l ON c.id = l.country_id WHERE l.name = 'Poland Ekstraklasa';
SELECT home_team_goal, away_team_goal  FROM Match  JOIN League ON Match.league_id = League.id  WHERE Match.date LIKE '2008-09-24%'  AND League.name = 'Belgium Jupiler League';
SELECT pa.sprint_speed, pa.agility, pa.acceleration  FROM Player_Attributes pa  JOIN Player p ON pa.player_api_id = p.player_api_id  WHERE p.player_name = 'Alexis Blin';
SELECT buildUpPlaySpeedClass  FROM Team_Attributes  WHERE team_api_id = (SELECT team_api_id FROM Team WHERE team_long_name = 'KSV Cercle Brugge')  LIMIT 1;
SELECT COUNT(*)  FROM Match m JOIN League l ON m.league_id = l.id WHERE l.name = 'Italy Serie A' AND m.season = '2015/2016';
SELECT MAX(home_team_goal)  FROM Match  WHERE league_id IN (SELECT id FROM League WHERE name LIKE '%Eredivisie%' OR name LIKE '%Netherlands%');
SELECT pa.finishing, pa.curve FROM Player_Attributes pa JOIN Player p ON pa.player_api_id = p.player_api_id WHERE p.weight = (SELECT MAX(weight) FROM Player);
SELECT l.name FROM Match m JOIN League l ON m.league_id = l.id WHERE m.season = '2015/2016' GROUP BY l.name ORDER BY COUNT(m.id) DESC LIMIT 4;
SELECT T.team_long_name FROM Team T JOIN Match M ON T.team_api_id = M.away_team_api_id ORDER BY M.away_team_goal DESC LIMIT 1;
SELECT player_name  FROM Player  JOIN Player_Attributes ON Player.player_api_id = Player_Attributes.player_api_id  ORDER BY overall_rating DESC  LIMIT 1;
SELECT CAST(COUNT(CASE WHEN p.height < 180 AND pa.overall_rating > 70 THEN 1 END) AS FLOAT) * 100 / COUNT(*) AS percentage FROM Player p JOIN Player_Attributes pa ON p.player_api_id = pa.player_api_id;
SELECT      CASE          WHEN COUNT(CASE WHEN SEX = 'M' AND Admission = '+' THEN 1 END) > COUNT(CASE WHEN SEX = 'M' AND Admission = '-' THEN 1 END) THEN 'in-patient'         WHEN COUNT(CASE WHEN SEX = 'M' AND Admission = '+' THEN 1 END) < COUNT(CASE WHEN SEX = 'M' AND Admission = '-' THEN 1 END) THEN 'outpatient'         ELSE 'equal'     END AS more_common,     CASE          WHEN COUNT(CASE WHEN SEX = 'M' AND Admission = '-') = 0 THEN NULL         ELSE ABS(COUNT(CASE WHEN SEX = 'M' AND Admission = '+') - COUNT(CASE WHEN SEX = 'M' AND Admission = '-')) * 100.0 / COUNT(CASE WHEN SEX = 'M' AND Admission = '-')     END AS percentage_deviation FROM Patient WHERE SEX = 'M';
SELECT CAST(COUNT(CASE WHEN strftime('%Y', Birthday) > '1930' AND SEX = 'F' THEN 1 END) AS FLOAT) * 100 / COUNT(CASE WHEN SEX = 'F' THEN 1 END) FROM Patient;
