SELECT p.ID, p.SEX, p.Birthday, l.`T-BIL` FROM Patient p JOIN Laboratory l ON p.ID = l.ID WHERE l.`T-BIL` = (SELECT MAX(`T-BIL`) FROM Laboratory);
