SELECT      CAST(COUNT(CASE WHEN time IS NOT NULL THEN raceId END) AS FLOAT) / COUNT(raceId) AS completion_rate FROM results WHERE raceId = (     SELECT raceId     FROM races     WHERE year = 2008 AND name = 'Australian Grand Prix' ); 
SELECT      ((CAST((last_driver_seconds - champion_seconds) AS REAL) / last_driver_seconds) * 100) AS percentage_faster FROM (     SELECT          CAST(SUBSTR(r1.time, 1, 2) AS INTEGER) * 3600 +         CAST(SUBSTR(r1.time, 4, 2) AS INTEGER) * 60 +         CAST(SUBSTR(r1.time, 7) AS REAL) AS champion_seconds,         CAST(SUBSTR(r2.time, 1, 2) AS INTEGER) * 3600 +         CAST(SUBSTR(r2.time, 4, 2) AS INTEGER) * 60 +         CAST(SUBSTR(r2.time, 7) AS REAL) AS last_driver_seconds     FROM results r1     JOIN results r2 ON r1.raceId = r2.raceId     JOIN races ra ON r1.raceId = ra.raceId     WHERE ra.name = 'Australian Grand Prix'         AND ra.year = 2008         AND r1.position = 1         AND r2.position = (             SELECT MAX(position)             FROM results             WHERE raceId = r1.raceId                 AND time IS NOT NULL         )         AND r1.time IS NOT NULL         AND r2.time IS NOT NULL ); 
