SELECT      (SELECT SUM(Consumption)       FROM yearmonth       JOIN customers ON yearmonth.CustomerID = customers.CustomerID       WHERE SUBSTR(Date, 1, 4) = '2012' AND Currency = 'CZK')      -      (SELECT SUM(Consumption)       FROM yearmonth       JOIN customers ON yearmonth.CustomerID = customers.CustomerID       WHERE SUBSTR(Date, 1, 4) = '2012' AND Currency = 'EUR')      AS Difference_in_Consumption; 
SELECT      SUBSTR(y.Date, 1, 4) AS Year,      SUM(y.Consumption) AS TotalGasUse FROM      yearmonth y JOIN      customers c ON y.CustomerID = c.CustomerID WHERE      c.Currency = 'EUR' GROUP BY      Year ORDER BY      TotalGasUse DESC LIMIT 1; 
