SELECT SUBSTR(Date, 1, 6) AS Month, SUM(Consumption) AS TotalConsumption FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'SME') AND SUBSTR(Date, 1, 4) = '2013' GROUP BY Month ORDER BY TotalConsumption DESC LIMIT 1; 
WITH SegmentConsumption2013 AS (     SELECT          c.Segment,         ym.CustomerID,         SUM(ym.Consumption) AS TotalConsumption     FROM          yearmonth ym     JOIN          customers c ON ym.CustomerID = c.CustomerID     WHERE          SUBSTR(ym.Date, 1, 6) BETWEEN '201301' AND '201312'     GROUP BY          c.Segment, ym.CustomerID ), LowestConsumptionPerSegment AS (     SELECT          Segment,         MIN(TotalConsumption) AS LowestConsumption     FROM          SegmentConsumption2013     GROUP BY          Segment ), AnnualAverageConsumption AS (     SELECT          sc.Segment,         lc.LowestConsumption AS TotalConsumption,         COUNT(DISTINCT sc.CustomerID) AS CustomerCount,         lc.LowestConsumption / COUNT(DISTINCT sc.CustomerID) AS AnnualAverage     FROM          SegmentConsumption2013 sc     JOIN          LowestConsumptionPerSegment lc ON sc.Segment = lc.Segment AND sc.TotalConsumption = lc.LowestConsumption     GROUP BY          sc.Segment, lc.LowestConsumption ) SELECT      SME.AnnualAverage - LAM.AnnualAverage AS SME_LAM_Difference FROM      AnnualAverageConsumption SME     JOIN AnnualAverageConsumption LAM ON SME.Segment = 'SME' AND LAM.Segment = 'LAM'; 
SELECT c.Segment,        SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2012' THEN y.Consumption ELSE 0 END) AS Consumption_2012,        SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2013' THEN y.Consumption ELSE 0 END) AS Consumption_2013,        ((SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2013' THEN y.Consumption ELSE 0 END) -           SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2012' THEN y.Consumption ELSE 0 END)) /           SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2013' THEN y.Consumption ELSE 0 END)) * 100 AS Percentage_Increase FROM customers c JOIN yearmonth y ON c.CustomerID = y.CustomerID WHERE c.Currency = 'EUR' GROUP BY c.Segment ORDER BY Percentage_Increase DESC; 
