SELECT m.first_name, m.last_name, maj.major_name FROM member m JOIN expense e ON m.member_id = e.link_to_member JOIN major maj ON m.link_to_major = maj.major_id WHERE e.cost > 100;
SELECT DISTINCT z.city, z.county FROM event e JOIN attendance a ON e.event_id = a.link_to_event JOIN member m ON a.link_to_member = m.member_id JOIN zip_code z ON m.zip = z.zip_code JOIN income i ON m.member_id = i.link_to_member GROUP BY e.event_id HAVING COUNT(i.income_id) > 40;
SELECT m.member_id, m.first_name, m.last_name FROM member m JOIN expense e ON m.member_id = e.link_to_member JOIN budget b ON e.link_to_budget = b.budget_id JOIN event ev ON b.link_to_event = ev.event_id GROUP BY m.member_id, m.first_name, m.last_name HAVING COUNT(DISTINCT ev.event_id) > 1 ORDER BY SUM(e.cost) DESC LIMIT 1;
SELECT AVG(e.cost)  FROM expense e  JOIN member m ON e.link_to_member = m.member_id  WHERE m.position != 'Member';
SELECT event_name  FROM event  JOIN budget ON event.event_id = budget.link_to_event  JOIN expense ON budget.budget_id = expense.link_to_budget  WHERE budget.category = 'Parking'    AND expense.cost < (SELECT AVG(expense.cost)                        FROM expense                        JOIN budget ON expense.link_to_budget = budget.budget_id                        WHERE budget.category = 'Parking');
SELECT (SUM(e.cost) * 100.0 / COUNT(ev.event_id)) AS percentage FROM event ev LEFT JOIN budget b ON ev.event_id = b.link_to_event LEFT JOIN expense e ON b.budget_id = e.link_to_budget WHERE ev.type = 'Meeting';
SELECT b.budget_id FROM budget b JOIN expense e ON b.budget_id = e.link_to_budget WHERE e.expense_description = 'Water, chips, cookies' ORDER BY e.cost DESC LIMIT 1;
SELECT m.first_name, m.last_name FROM member m JOIN expense e ON m.member_id = e.link_to_member GROUP BY m.member_id ORDER BY SUM(e.cost) DESC LIMIT 5;
SELECT m.first_name, m.last_name, m.phone FROM member m JOIN expense e ON m.member_id = e.link_to_member WHERE e.cost > (SELECT AVG(cost) FROM expense);
SELECT (SUM(CASE WHEN z.state = 'New Jersey' THEN 1 ELSE 0 END) * 1.0 / COUNT(*)) - (SUM(CASE WHEN z.state = 'Vermont' THEN 1 ELSE 0 END) * 1.0 / COUNT(*)) AS difference FROM member m JOIN zip_code z ON m.zip = z.zip_code;
SELECT m.major_name, m.department FROM member me JOIN major m ON me.link_to_major = m.major_id WHERE me.first_name = 'Garrett' AND me.last_name = 'Gerke';
SELECT m.first_name, m.last_name, e.cost FROM member m JOIN expense e ON m.member_id = e.link_to_member WHERE e.expense_description = 'Water, Veggie tray, supplies';
SELECT m.last_name, m.phone FROM member m JOIN major ma ON m.link_to_major = ma.major_id WHERE ma.major_name = 'Elementary Education';
SELECT b.category, b.amount FROM budget b JOIN event e ON b.link_to_event = e.event_id WHERE e.event_name = 'January Speaker';
SELECT e.event_name FROM event e JOIN budget b ON e.event_id = b.link_to_event WHERE b.category = 'Food';
SELECT m.first_name, m.last_name, i.amount  FROM member m  JOIN income i ON m.member_id = i.link_to_member  WHERE i.date_received = '9/9/2019';
SELECT b.category FROM budget b JOIN expense e ON b.budget_id = e.link_to_budget WHERE e.expense_description = 'Posters';
SELECT m.first_name, m.last_name, maj.college FROM member m JOIN major maj ON m.link_to_major = maj.major_id WHERE m.position = 'Secretary';
SELECT SUM(b.spent) AS total_spent, e.event_name FROM budget b JOIN event e ON b.link_to_event = e.event_id WHERE b.category = 'Speaker Gifts' GROUP BY e.event_name;
SELECT z.city FROM member m JOIN zip_code z ON m.zip = z.zip_code WHERE m.first_name = 'Garrett' AND m.last_name = 'Gerke';
SELECT m.first_name, m.last_name, m.position FROM member m JOIN zip_code z ON m.zip = z.zip_code WHERE z.city = 'Lincolnton' AND z.state = 'North Carolina' AND z.zip_code = 28092;
SELECT COUNT(*)  FROM gasstations  WHERE Country = 'CZE' AND Segment = 'Premium';
SELECT      CAST(SUM(CASE WHEN Currency = 'EUR' THEN 1 ELSE 0 END) AS FLOAT) /      NULLIF(SUM(CASE WHEN Currency = 'CZK' THEN 1 ELSE 0 END), 0) AS ratio FROM customers;
SELECT yearmonth.CustomerID FROM yearmonth JOIN customers ON yearmonth.CustomerID = customers.CustomerID WHERE substr(yearmonth.Date, 1, 4) = '2012'   AND customers.Segment = 'LAM' ORDER BY yearmonth.Consumption ASC LIMIT 1; 
SELECT AVG(y.Consumption) / 12 FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE c.Segment = 'SME'   AND y.Date BETWEEN '201301' AND '201312';
SELECT CustomerID FROM yearmonth WHERE Date BETWEEN '201101' AND '201112'   AND CustomerID IN (SELECT CustomerID FROM customers WHERE Currency = 'CZK') ORDER BY Consumption DESC LIMIT 1;
SELECT COUNT(*)  FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE c.Segment = 'KAM'    AND y.Date BETWEEN '201201' AND '201212'    AND y.Consumption < 30000;
SELECT      SUM(CASE WHEN c.Currency = 'CZK' THEN y.Consumption ELSE 0 END) - SUM(CASE WHEN c.Currency = 'EUR' THEN y.Consumption ELSE 0 END) FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE SUBSTR(y.Date, 1, 4) = '2012';
SELECT SUBSTR(Date, 1, 4) AS Year FROM transactions_1k t JOIN customers c ON t.CustomerID = c.CustomerID WHERE c.Currency = 'EUR' GROUP BY SUBSTR(Date, 1, 4) ORDER BY SUM(t.Amount) DESC LIMIT 1;
SELECT Segment  FROM customers  WHERE CustomerID IN (     SELECT CustomerID      FROM yearmonth      ORDER BY Consumption ASC      LIMIT 1 )
SELECT SUBSTR(Date, 1, 4) AS year FROM yearmonth JOIN customers ON yearmonth.CustomerID = customers.CustomerID WHERE customers.Currency = 'CZK' GROUP BY SUBSTR(Date, 1, 4) ORDER BY SUM(Consumption) DESC LIMIT 1;
SELECT SUBSTR(Date, 5, 2) AS Month FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE c.Segment = 'SME' AND Date BETWEEN '201301' AND '201312' ORDER BY Consumption DESC LIMIT 1;
SELECT      (SELECT AVG(Consumption) FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'SME') AND Date LIKE '2013%') -      (SELECT AVG(Consumption) FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'LAM') AND Date LIKE '2013%') AS SME_LAM_Diff,     (SELECT AVG(Consumption) FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'LAM') AND Date LIKE '2013%') -      (SELECT AVG(Consumption) FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'KAM') AND Date LIKE '2013%') AS LAM_KAM_Diff,     (SELECT AVG(Consumption) FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'KAM') AND Date LIKE '2013%') -      (SELECT AVG(Consumption) FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'SME') AND Date LIKE '2013%') AS KAM_SME_Diff
SELECT      c.Segment,     ((SUM(CASE WHEN SUBSTR(ym.Date, 1, 4) = '2013' THEN ym.Consumption ELSE 0 END) - SUM(CASE WHEN SUBSTR(ym.Date, 1, 4) = '2012' THEN ym.Consumption ELSE 0 END)) * 100.0 / SUM(CASE WHEN SUBSTR(ym.Date, 1, 4) = '2013' THEN ym.Consumption ELSE 0 END)) AS percentage_increase FROM      yearmonth ym JOIN      customers c ON ym.CustomerID = c.CustomerID WHERE      c.Currency = 'EUR' AND c.Segment IN ('SME', 'LAM', 'KAM') GROUP BY      c.Segment ORDER BY      percentage_increase DESC;
SELECT SUM(Consumption)  FROM yearmonth  WHERE CustomerID = 6    AND Date BETWEEN '201308' AND '201311';
SELECT      (SELECT COUNT(*) FROM gasstations WHERE Country = 'CZE' AND Segment = 'Value for money') -      (SELECT COUNT(*) FROM gasstations WHERE Country = 'SVK' AND Segment = 'Value for money');
SELECT      (SELECT Consumption FROM yearmonth WHERE CustomerID = 7 AND Date = '201304') -      (SELECT Consumption FROM yearmonth WHERE CustomerID = 5 AND Date = '201304') AS consumption_difference;
SELECT      SUM(CASE WHEN c.Currency = 'CZK' AND c.Segment = 'SME' THEN 1 ELSE 0 END) -      SUM(CASE WHEN c.Currency = 'EUR' AND c.Segment = 'SME' THEN 1 ELSE 0 END) AS difference FROM customers c WHERE c.Segment = 'SME';
SELECT c.CustomerID FROM customers c JOIN yearmonth y ON c.CustomerID = y.CustomerID WHERE c.Segment = 'LAM' AND c.Currency = 'EUR' AND y.Date = '201310' ORDER BY y.Consumption DESC LIMIT 1;
SELECT CustomerID, MAX(Consumption) AS max_consumption FROM yearmonth WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'KAM') GROUP BY CustomerID ORDER BY max_consumption DESC LIMIT 1;
SELECT SUM(Consumption)  FROM yearmonth  JOIN customers ON yearmonth.CustomerID = customers.CustomerID  WHERE customers.Segment = 'KAM' AND yearmonth.Date = '201305';
SELECT (COUNT(CASE WHEN y.Consumption > 46.73 THEN 1 END) * 100.0 / COUNT(*))  FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE c.Segment = 'LAM';
SELECT Country, COUNT(*) AS Total_Value_For_Money_Gas_Stations FROM gasstations WHERE Segment = 'Value for money' GROUP BY Country ORDER BY Total_Value_For_Money_Gas_Stations DESC;
SELECT (COUNT(CASE WHEN Currency = 'EUR' THEN 1 END) * 100.0 / COUNT(*)) AS percentage FROM customers WHERE Segment = 'KAM';
SELECT      (COUNT(CASE WHEN ym.Consumption > 528.3 THEN 1 END) * 100.0 / COUNT(*)) AS percentage FROM      yearmonth ym WHERE      ym.Date = '201202';
SELECT (COUNT(CASE WHEN Segment = 'Premium' THEN 1 END) * 100.0 / COUNT(*))  FROM gasstations  WHERE Country = 'SVK';
SELECT CustomerID FROM yearmonth WHERE Date = '201309' ORDER BY Consumption DESC LIMIT 1;
SELECT c.Segment FROM customers c JOIN yearmonth y ON c.CustomerID = y.CustomerID WHERE y.Date = '201309' ORDER BY y.Consumption ASC LIMIT 1;
SELECT c.CustomerID FROM customers c JOIN yearmonth y ON c.CustomerID = y.CustomerID WHERE c.Segment = 'SME' AND y.Date = '201206' ORDER BY y.Consumption ASC LIMIT 1;
SELECT MAX(Consumption)  FROM yearmonth  WHERE SUBSTR(Date, 1, 4) = '2012';
SELECT MAX(total_consumption) AS biggest_monthly_consumption FROM (     SELECT CustomerID, SUM(Consumption) / 12 AS total_consumption     FROM yearmonth     WHERE CustomerID IN (SELECT CustomerID FROM customers WHERE Currency = 'EUR')     GROUP BY CustomerID );
SELECT DISTINCT p.Description FROM products p JOIN transactions_1k t ON p.ProductID = t.ProductID WHERE SUBSTR(t.Date, 1, 4) = '2013' AND SUBSTR(t.Date, 6, 2) = '09';
