SELECT T1.ProductID  FROM transactions_1k AS T1  WHERE T1.Date = '2012-08-23' AND T1.Time = '21:20:00';
SELECT y.Date, y.Consumption  FROM yearmonth y  JOIN customers c ON y.CustomerID = c.CustomerID  WHERE y.Date LIKE '201201%'    AND c.CustomerID IN (     SELECT CustomerID      FROM transactions_1k      WHERE Date = '2012-08-24' AND Price = 124.05   );
SELECT COUNT(*)  FROM transactions_1k t JOIN gasstations g ON t.GasStationID = g.GasStationID WHERE t.Date = '2012-08-26'    AND t.Time BETWEEN '08:00:00' AND '09:00:00'   AND g.Country = 'CZE';
SELECT c.Currency FROM customers c JOIN yearmonth y ON c.CustomerID = y.CustomerID WHERE y.Date = '201306' AND y.Consumption = 214582.17;
SELECT g.Country FROM transactions_1k t JOIN customers c ON t.CustomerID = c.CustomerID JOIN gasstations g ON t.GasStationID = g.GasStationID WHERE t.CardID = 667467 LIMIT 1;
SELECT c.Currency FROM customers c JOIN transactions_1k t ON c.CustomerID = t.CustomerID WHERE t.Date = '2012-08-24' AND t.Price = 548.4;
SELECT      (COUNT(CASE WHEN c.Currency = 'EUR' THEN 1 END) * 100.0 / COUNT(*)) AS percentage FROM      customers c JOIN      transactions_1k t ON c.CustomerID = t.CustomerID WHERE      t.Date = '2012-08-25';
SELECT (t1.Consumption - t2.Consumption) / t1.Consumption AS decrease_rate FROM yearmonth t1 JOIN yearmonth t2 ON t1.CustomerID = t2.CustomerID JOIN transactions_1k t3 ON t1.CustomerID = t3.CustomerID WHERE t3.Date = '2012-08-25' AND t3.Price = 634.8 AND t1.Date LIKE '2012%' AND t2.Date LIKE '2013%';
SELECT GasStationID FROM transactions_1k GROUP BY GasStationID ORDER BY SUM(Amount * Price) DESC LIMIT 1;
SELECT      (COUNT(CASE WHEN Segment = 'Premium' THEN 1 END) * 100.0 / COUNT(*)) AS percentage_premium FROM      gasstations WHERE      Country = 'SVK';
SELECT SUM(t.Price) AS TotalSpent, SUM(CASE WHEN y.Date = '201201' THEN y.Consumption ELSE 0 END) AS SpentInJanuary2012 FROM transactions_1k t LEFT JOIN yearmonth y ON t.CustomerID = y.CustomerID AND y.Date = '201201' WHERE t.CustomerID = 38508;
SELECT p.Description FROM transactions_1k t JOIN products p ON t.ProductID = p.ProductID GROUP BY t.ProductID ORDER BY SUM(t.Amount) DESC LIMIT 5;
SELECT      t.CustomerID,     SUM(t.Price) / SUM(t.Amount) AS average_price_per_item,     c.Currency FROM      transactions_1k t JOIN      customers c ON t.CustomerID = c.CustomerID GROUP BY      t.CustomerID, c.Currency ORDER BY      SUM(t.Price) DESC LIMIT 1;
SELECT g.Country FROM gasstations g JOIN transactions_1k t ON g.GasStationID = t.GasStationID WHERE t.ProductID = 2 ORDER BY t.Price DESC LIMIT 1;
