SELECT COUNT(*)  FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds WHERE s.County = 'Fresno'    AND s.FundingType = 'Directly Funded'   AND sat.NumTstTakr <= 250;
SELECT s.Phone FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds ORDER BY sat.AvgScrMath DESC LIMIT 1;
SELECT COUNT(*)  FROM frpm  WHERE `County Name` = 'Amador'    AND `Low Grade` = '9'    AND `High Grade` = '12';
SELECT COUNT(*)  FROM frpm  WHERE `County Name` = 'Los Angeles'    AND `Free Meal Count (K-12)` > 500    AND `FRPM Count (K-12)` < 700;
SELECT s.School FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds WHERE s.County = 'Contra Costa' ORDER BY sat.NumTstTakr DESC LIMIT 1;
SELECT      s.School,      s.Street || ', ' || s.City || ', ' || s.State || ' ' || s.Zip AS FullStreetAddress FROM      schools s JOIN      frpm f ON s.CDSCode = f.CDSCode WHERE      ABS(f.`Enrollment (K-12)` - f.`Enrollment (Ages 5-17)`) > 30;
SELECT DISTINCT s.School FROM schools s JOIN frpm f ON s.CDSCode = f.CDSCode JOIN satscores sat ON s.CDSCode = sat.cds WHERE (f.`Free Meal Count (K-12)` / f.`Enrollment (K-12)`) > 0.1 AND sat.NumGE1500 > 0;
SELECT sch.School, sch.FundingType FROM schools sch JOIN (     SELECT cds     FROM satscores     WHERE cname = 'Riverside'       AND AvgScrMath IS NOT NULL     GROUP BY cds     HAVING AVG(AvgScrMath) > 400 ) filtered ON sch.CDSCode = filtered.cds WHERE sch.County = 'Riverside'; 
SELECT s.School, s.Street, s.City, s.State, s.Zip FROM schools s JOIN frpm f ON s.CDSCode = f.`CDSCode` WHERE s.GSoffered LIKE '%12%'   AND f.`County Name` = 'Monterey'   AND f.`FRPM Count (Ages 5-17)` > 800;
SELECT      s.School,     sat.AvgScrWrite,     s.Phone FROM      schools s JOIN      satscores sat ON s.CDSCode = sat.cds WHERE      s.OpenDate > '1991-12-31' OR s.ClosedDate < '2000-01-01';
