SELECT OpenDate FROM schools JOIN frpm ON schools.CDSCode = frpm.CDSCode ORDER BY `Enrollment (K-12)` DESC LIMIT 1;
SELECT `City` FROM `schools` s JOIN `frpm` f ON s.`CDSCode` = f.`CDSCode` ORDER BY `Enrollment (K-12)` ASC LIMIT 5;
SELECT `Percent (%) Eligible Free (K-12)` FROM frpm ORDER BY `Enrollment (K-12)` DESC LIMIT 2 OFFSET 8;
