SELECT s.Website, s.School FROM schools s JOIN frpm f ON s.CDSCode = f.CDSCode WHERE f.`Free Meal Count (Ages 5-17)` BETWEEN 1900 AND 2000;
SELECT (`Free Meal Count (Ages 5-17)` / `Enrollment (Ages 5-17)`) AS free_rate FROM frpm JOIN schools ON frpm.CDSCode = schools.CDSCode WHERE (schools.AdmFName1 || ' ' || schools.AdmLName1 = 'Kacey Gibson'    OR schools.AdmFName2 || ' ' || schools.AdmLName2 = 'Kacey Gibson'    OR schools.AdmFName3 || ' ' || schools.AdmLName3 = 'Kacey Gibson');
SELECT s.AdmEmail1 FROM schools s JOIN frpm f ON s.CDSCode = f.CDSCode WHERE f.`Charter School (Y/N)` = 1 ORDER BY f.`Enrollment (K-12)` LIMIT 1;
SELECT AdmFName1, AdmLName1 FROM schools JOIN satscores ON schools.CDSCode = satscores.cds ORDER BY satscores.NumGE1500 DESC LIMIT 1;
