SELECT s.Website FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds JOIN frpm f ON s.CDSCode = f.CDSCode WHERE f.`County Name` = 'Los Angeles'   AND sat.NumTstTakr BETWEEN 2000 AND 3000;
