SELECT s.AvgScrWrite, sch.City FROM satscores s JOIN schools sch ON s.cds = sch.CDSCode WHERE s.NumGE1500 IS NOT NULL ORDER BY s.NumGE1500 DESC LIMIT 1;
SELECT s.School, sat.AvgScrWrite FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds WHERE s.AdmLName1 = 'Ulrich' AND s.AdmFName1 = 'Ricci'    OR s.AdmLName2 = 'Ulrich' AND s.AdmFName2 = 'Ricci'    OR s.AdmLName3 = 'Ulrich' AND s.AdmFName3 = 'Ricci';
