SELECT COUNT(*) * 1.0 / 12 AS monthly_average FROM schools WHERE County = 'Alameda'   AND DOC = '52'   AND OpenDate >= '1980-01-01'   AND OpenDate < '1981-01-01';
SELECT      CAST(SUM(CASE WHEN s.DOC = '54' AND s.County = 'Orange' THEN 1 ELSE 0 END) AS REAL) /      SUM(CASE WHEN s.DOC = '52' THEN 1 ELSE 0 END) AS ratio FROM schools s;
SELECT County, School, ClosedDate FROM schools WHERE StatusType = 'Closed' ORDER BY County, ClosedDate DESC;
SELECT s.MailStreet, s.School FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds ORDER BY sat.AvgScrMath DESC LIMIT 1 OFFSET 6;
