SELECT      CAST(SUM(CASE WHEN s.County = 'Colusa' THEN 1 ELSE 0 END) AS FLOAT) / SUM(CASE WHEN s.County = 'Humboldt' THEN 1 ELSE 0 END) AS ratio FROM schools s WHERE s.MailState = 'CA';
SELECT COUNT(*)  FROM schools  WHERE MailState = 'CA'    AND City = 'San Joaquin'    AND StatusType = 'Active';
SELECT s.Phone, s.Ext FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds ORDER BY sat.AvgScrWrite DESC LIMIT 1 OFFSET 332;
SELECT s.Phone, s.Ext, s.School  FROM schools s  WHERE s.Zip = '95203-3704';
SELECT Website  FROM schools  WHERE (AdmFName1 = 'Mike' AND AdmLName1 = 'Larson')     OR (AdmFName2 = 'Mike' AND AdmLName2 = 'Larson')     OR (AdmFName3 = 'Mike' AND AdmLName3 = 'Larson')    OR (AdmFName1 = 'Dante' AND AdmLName1 = 'Alvarez')     OR (AdmFName2 = 'Dante' AND AdmLName2 = 'Alvarez')     OR (AdmFName3 = 'Dante' AND AdmLName3 = 'Alvarez');
SELECT Website  FROM schools  WHERE Virtual = 'P'    AND Charter = 1    AND County = 'San Joaquin';
SELECT COUNT(*)  FROM schools  WHERE City = 'Hickman'    AND DOC = '52'    AND Charter = 1;
SELECT COUNT(*)  FROM schools s JOIN frpm f ON s.CDSCode = f.CDSCode WHERE s.County = 'Los Angeles'   AND s.Charter = 0   AND (f.`Free Meal Count (K-12)` * 100.0 / f.`Enrollment (K-12)`) < 0.18;
