SELECT COUNT(*)  FROM schools  WHERE FundingType = 'Directly Funded'    AND County = 'Stanislaus'    AND OpenDate >= '2000-01-01'    AND OpenDate <= '2005-12-31';
SELECT      COUNT(*)  FROM      schools  WHERE      strftime('%Y', ClosedDate) = '1989'      AND City = 'San Francisco'      AND "District Type" = 'Community College District'; 
