SELECT NCESDist FROM schools WHERE SOC = '31';
SELECT      SUM(CASE WHEN s.StatusType = 'Active' THEN 1 ELSE 0 END) AS Active_Count,     SUM(CASE WHEN s.StatusType = 'Closed' THEN 1 ELSE 0 END) AS Closed_Count FROM schools s WHERE s.County = 'Alpine' AND s.DOCType = 'District Community Day School';
SELECT `District Code`  FROM frpm  JOIN schools ON frpm.`CDSCode` = schools.`CDSCode`  WHERE schools.City = 'Fresno' AND schools.Magnet = 0;
