SELECT `FRPM Count (Ages 5-17)`  FROM frpm  JOIN schools ON frpm.CDSCode = schools.CDSCode  WHERE schools.School = 'Youth Authority School'  AND schools.MailStreet = 'PO Box 1040';
SELECT `Low Grade`  FROM frpm  JOIN schools ON frpm.CDSCode = schools.CDSCode  WHERE schools.EdOpsCode = 'SPECON' AND schools.NCESDist = '0613360';
SELECT s.School, s.GSserved FROM schools s JOIN frpm f ON s.CDSCode = f.CDSCode WHERE f.`NSLP Provision Status` = '2' AND f.`County Code` = '37';
SELECT s.City FROM schools s JOIN frpm f ON s.CDSCode = f.`CDSCode` WHERE f.`NSLP Provision Status` = '2'   AND f.`Low Grade` = '9'   AND f.`High Grade` = '12'   AND f.`County Name` = 'Merced'   AND s.EILCode = 'HS';
