SELECT GSserved FROM schools WHERE City = 'Adelanto' GROUP BY GSserved ORDER BY COUNT(*) DESC LIMIT 1;
SELECT County, COUNT(*) AS school_count FROM schools WHERE Virtual = 'F'   AND County IN ('San Diego', 'Santa Barbara') GROUP BY County ORDER BY school_count DESC LIMIT 1;
SELECT `School Type`, `School Name`, `Latitude`  FROM frpm  JOIN schools ON frpm.`CDSCode` = schools.`CDSCode`  ORDER BY `Latitude` DESC  LIMIT 1;
SELECT s.City, f.`Low Grade`, s.School FROM schools s JOIN frpm f ON s.CDSCode = f.CDSCode WHERE s.State = 'CA' ORDER BY s.Latitude ASC LIMIT 1;
SELECT GSoffered FROM schools ORDER BY ABS(Longitude) DESC LIMIT 1;
SELECT      COUNT(CASE WHEN `NSLP Provision Status` = 'Multiple Provision Types' THEN 1 END) AS schools_with_multiple_provision_types,     City,     COUNT(*) AS number_of_schools FROM      schools s JOIN      frpm f ON s.CDSCode = f.CDSCode WHERE      s.Magnet = 1      AND f.`Low Grade` = 'K'      AND f.`High Grade` = '8' GROUP BY      City;
SELECT AdmFName1, District FROM schools WHERE AdmFName1 IS NOT NULL GROUP BY AdmFName1, District ORDER BY COUNT(*) DESC LIMIT 2;
SELECT      f.`Percent (%) Eligible Free (K-12)`,      f.`District Code` FROM      frpm f JOIN      schools s ON f.CDSCode = s.CDSCode WHERE      s.AdmFName1 = 'Alusine' OR s.AdmFName2 = 'Alusine' OR s.AdmFName3 = 'Alusine';
SELECT s.AdmLName1, s.District, s.County, s.School FROM schools s WHERE s.CharterNum = '40';
SELECT AdmEmail1, AdmEmail2, AdmEmail3 FROM schools WHERE County = 'San Bernardino'   AND District = 'San Bernardino City Unified'   AND OpenDate BETWEEN '2009-01-01' AND '2010-12-31'   AND SOC = '62'   AND DOC = '54';
SELECT s.AdmEmail1, s.School FROM schools s JOIN satscores sat ON s.CDSCode = sat.cds WHERE sat.NumGE1500 IS NOT NULL ORDER BY sat.NumGE1500 DESC LIMIT 1;
SELECT COUNT(*)  FROM account a JOIN district d ON a.district_id = d.district_id WHERE a.frequency = 'POPLATEK PO OBRATU' AND d.A3 = 'East Bohemia';
SELECT COUNT(*)  FROM account a JOIN district d ON a.district_id = d.district_id WHERE d.A3 LIKE '%Prague%' AND a.account_id IN (SELECT DISTINCT account_id FROM loan WHERE status IN ('A', 'B'));
SELECT      CASE          WHEN AVG(A12) > AVG(A13) THEN '1995'         WHEN AVG(A13) > AVG(A12) THEN '1996'         ELSE 'Equal'     END AS higher_unemployment_year FROM district;
SELECT COUNT(DISTINCT d.district_id) FROM district d JOIN client c ON d.district_id = c.district_id WHERE c.gender = 'F' GROUP BY d.district_id HAVING AVG(d.A11) > 6000 AND AVG(d.A11) < 10000;
SELECT COUNT(*)  FROM client c JOIN district d ON c.district_id = d.district_id WHERE c.gender = 'M'    AND d.A3 = 'North Bohemia'    AND d.A11 > 8000;
SELECT      a.account_id,     (SELECT MAX(A11) FROM district) - (SELECT MIN(A11) FROM district) AS gap FROM      account a JOIN      disp d ON a.account_id = d.account_id JOIN      client c ON d.client_id = c.client_id JOIN      district dist ON c.district_id = dist.district_id WHERE      c.gender = 'F' ORDER BY      c.birth_date ASC,      dist.A11 ASC LIMIT 1;
SELECT a.account_id FROM account a JOIN client c ON a.district_id = c.district_id JOIN district d ON c.district_id = d.district_id ORDER BY c.birth_date DESC, d.A11 DESC LIMIT 1;
SELECT COUNT(*)  FROM account a JOIN disp d ON a.account_id = d.account_id WHERE a.frequency = 'POPLATEK TYDNE' AND d.type = 'OWNER';
SELECT DISTINCT c.client_id FROM client c JOIN disp d ON c.client_id = d.client_id JOIN account a ON d.account_id = a.account_id WHERE d.type = 'DISPONENT' AND a.frequency = 'POPLATEK PO OBRATU';
SELECT a.account_id FROM account a JOIN loan l ON a.account_id = l.account_id WHERE l.status = 'A'   AND strftime('%Y', l.date) = '1997'   AND a.frequency = 'POPLATEK TYDNE' ORDER BY l.amount ASC LIMIT 1;
SELECT a.account_id FROM account a JOIN loan l ON a.account_id = l.account_id WHERE l.duration > 12   AND strftime('%Y', a.date) = '1993' ORDER BY l.amount DESC LIMIT 1;
SELECT COUNT(*)  FROM client c JOIN district d ON c.district_id = d.district_id WHERE c.gender = 'F'    AND c.birth_date < '1950-01-01'   AND d.A2 = 'Sokolov';
SELECT account_id FROM account WHERE strftime('%Y', date) = '1995' ORDER BY date ASC LIMIT 1;
SELECT DISTINCT a.account_id FROM account a JOIN trans t ON a.account_id = t.account_id WHERE a.date < '1997-01-01'   AND t.balance > 3000;
SELECT client_id FROM disp WHERE disp_id IN (SELECT disp_id FROM card WHERE issued = '1994-03-03');
SELECT date FROM account WHERE account_id = (SELECT account_id FROM trans WHERE amount = 840 AND date = '1998-10-14');
SELECT a.district_id FROM account a JOIN loan l ON a.account_id = l.account_id WHERE l.date = '1994-08-25';
SELECT MAX(t.amount)  FROM trans t  JOIN account a ON t.account_id = a.account_id  JOIN disp d ON a.account_id = d.account_id  JOIN card c ON d.disp_id = c.disp_id  WHERE c.issued = '1996-10-21';
