SELECT disp_id FROM disp WHERE account_id IN (SELECT account_id FROM trans WHERE amount = 5100 AND date = '1998-09-02');
SELECT COUNT(*)  FROM account a JOIN district d ON a.district_id = d.district_id WHERE d.A2 = 'Litomerice' AND strftime('%Y', a.date) = '1996';
SELECT d.A2 FROM client c JOIN account a ON c.client_id = a.district_id JOIN district d ON a.district_id = d.district_id WHERE c.gender = 'F' AND c.birth_date = '1976-01-29';
SELECT c.birth_date FROM client c JOIN disp d ON c.client_id = d.client_id JOIN account a ON d.account_id = a.account_id JOIN loan l ON a.account_id = l.account_id WHERE l.amount = 98832   AND l.date = '1996-01-03'; 
SELECT a.account_id FROM account a JOIN district d ON a.district_id = d.district_id WHERE d.A3 = 'Prague' ORDER BY a.date LIMIT 1;
SELECT (COUNT(CASE WHEN c.gender = 'M' THEN 1 END) * 100.0 / COUNT(c.client_id))  FROM client c  JOIN district d ON c.district_id = d.district_id  WHERE d.A3 = 'South Bohemia'  ORDER BY d.A4 DESC  LIMIT 1;
