SELECT (SUM(CASE WHEN status = 'A' THEN amount ELSE 0 END) * 100.0 / SUM(amount)) AS percentage FROM loan;
SELECT      (SUM(CASE WHEN status = 'C' AND amount < 100000 THEN 1 ELSE 0 END) * 100.0 / COUNT(*)) AS percentage FROM loan WHERE amount < 100000;
SELECT a.account_id, d.A2, d.A3 FROM account a JOIN district d ON a.district_id = d.district_id WHERE strftime('%Y', a.date) = '1993'   AND a.frequency = 'POPLATEK PO OBRATU';
SELECT a.account_id, a.frequency FROM account a JOIN client c ON d.client_id = c.client_id JOIN disp d ON a.account_id = d.account_id JOIN district dis ON a.district_id = dis.district_id WHERE strftime('%Y', a.date) BETWEEN '1995' AND '2000'   AND dis.A3 LIKE '%east Bohemia%'   AND d.type = 'OWNER';
SELECT account_id, date  FROM account  JOIN district ON account.district_id = district.district_id  WHERE district.A2 = 'Prachatice';
