WITH SegmentConsumption AS (     SELECT          c.Segment,         y.CustomerID,         SUM(y.Consumption) AS TotalConsumption     FROM yearmonth y     JOIN customers c ON y.CustomerID = c.CustomerID     WHERE SUBSTR(y.Date, 1, 4) = '2013'     GROUP BY c.Segment, y.CustomerID ), LeastConsumption AS (     SELECT          Segment,         MIN(TotalConsumption) AS LeastTotalConsumption     FROM SegmentConsumption     GROUP BY Segment ), AnnualAverageConsumption AS (     SELECT          sc.Segment,         lc.LeastTotalConsumption / COUNT(sc.CustomerID) AS AnnualAverage     FROM SegmentConsumption sc     JOIN LeastConsumption lc ON sc.Segment = lc.Segment AND sc.TotalConsumption = lc.LeastTotalConsumption     GROUP BY sc.Segment, lc.LeastTotalConsumption ) SELECT      SME.AnnualAverage - LAM.AnnualAverage AS SME_vs_LAM,     LAM.AnnualAverage - KAM.AnnualAverage AS LAM_vs_KAM,     KAM.AnnualAverage - SME.AnnualAverage AS KAM_vs_SME FROM AnnualAverageConsumption SME JOIN AnnualAverageConsumption LAM ON SME.Segment = 'SME' AND LAM.Segment = 'LAM' JOIN AnnualAverageConsumption KAM ON KAM.Segment = 'KAM'; 
SELECT c.Segment,         ((SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2013' THEN y.Consumption ELSE 0 END) -           SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2012' THEN y.Consumption ELSE 0 END)) /           SUM(CASE WHEN SUBSTR(y.Date, 1, 4) = '2013' THEN y.Consumption ELSE 0 END)) * 100 AS PercentageIncrease FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE c.Currency = 'EUR' GROUP BY c.Segment ORDER BY PercentageIncrease DESC; 
SELECT SUM(Consumption) AS TotalConsumption FROM yearmonth WHERE CustomerID = 6 AND Date BETWEEN '201308' AND '201311'; 
SELECT      (SELECT COUNT(*) FROM gasstations WHERE Country = 'CZE' AND Segment = 'Value for money') -      (SELECT COUNT(*) FROM gasstations WHERE Country = 'SVK' AND Segment = 'Value for money') AS Difference; 
SELECT      (SELECT Consumption FROM yearmonth WHERE CustomerID = 7 AND Date = '201304')      -      (SELECT Consumption FROM yearmonth WHERE CustomerID = 5 AND Date = '201304') AS ConsumptionDifference; 
SELECT      (SELECT COUNT(*) FROM customers WHERE Segment = 'SME' AND Currency = 'CZK') -      (SELECT COUNT(*) FROM customers WHERE Segment = 'SME' AND Currency = 'EUR') AS Amount_of_More_SMEs; 
SELECT CustomerID  FROM yearmonth  WHERE Date = '201310' AND CustomerID IN (     SELECT CustomerID      FROM customers      WHERE Segment = 'LAM' AND Currency = 'EUR' )  ORDER BY Consumption DESC  LIMIT 1; 
SELECT c.CustomerID, SUM(y.Consumption) AS TotalConsumption FROM customers c JOIN yearmonth y ON c.CustomerID = y.CustomerID WHERE c.Segment = 'LAM' GROUP BY c.CustomerID ORDER BY TotalConsumption DESC LIMIT 1; 
SELECT SUM(Consumption) AS TotalConsumption FROM yearmonth JOIN customers ON yearmonth.CustomerID = customers.CustomerID WHERE customers.Segment = 'KAM' AND yearmonth.Date = '201305'; 
SELECT    (CAST(COUNT(DISTINCT y.CustomerID) AS FLOAT) / (SELECT COUNT(*) FROM customers WHERE Segment = 'LAM') * 100) AS Percentage FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE c.Segment = 'LAM' AND y.Consumption > 46.73; 
SELECT Country, COUNT(*) AS TotalValueForMoneyStations FROM gasstations WHERE Segment = 'Value for money' GROUP BY Country; 
SELECT      (CAST(SUM(CASE WHEN Currency = 'EUR' THEN 1 ELSE 0 END) AS FLOAT) / COUNT(*)) * 100 AS Percentage_KAM_Euro FROM customers WHERE Segment = 'KAM'; 
SELECT      (CAST(COUNT(*) AS FLOAT) / (SELECT COUNT(*) FROM yearmonth WHERE SUBSTR(Date, 1, 4) = '2012' AND SUBSTR(Date, 5, 2) = '02') * 100) AS Percentage FROM yearmonth WHERE SUBSTR(Date, 1, 4) = '2012' AND SUBSTR(Date, 5, 2) = '02' AND Consumption > 528.3; 
SELECT      (CAST(SUM(CASE WHEN Segment = 'Premium' THEN 1 ELSE 0 END) AS FLOAT) / COUNT(*)) * 100 AS PremiumPercentage FROM gasstations WHERE Country = 'SVK'; 
SELECT CustomerID, MAX(Consumption) AS MaxConsumption FROM yearmonth WHERE Date = '201309'; 
SELECT c.Segment, MIN(y.Consumption) AS MinConsumption FROM yearmonth y JOIN customers c ON y.CustomerID = c.CustomerID WHERE y.Date = '201309' GROUP BY c.Segment ORDER BY MinConsumption ASC LIMIT 1; 
SELECT CustomerID, MIN(Consumption) AS LeastConsumption FROM yearmonth WHERE Date = '201206' AND CustomerID IN (SELECT CustomerID FROM customers WHERE Segment = 'SME'); 
SELECT MAX(Consumption) AS HighestMonthlyConsumption FROM yearmonth WHERE SUBSTR(Date, 1, 4) = '2012'; 
SELECT MAX(TotalConsumption / 12) AS BiggestMonthlyConsumption FROM (     SELECT          yearmonth.CustomerID,         SUM(yearmonth.Consumption) AS TotalConsumption     FROM yearmonth     JOIN customers ON yearmonth.CustomerID = customers.CustomerID     WHERE customers.Currency = 'EUR'     GROUP BY yearmonth.CustomerID ) AS CustomerConsumption; 
