SELECT      ((SUM(CASE WHEN SUBSTR(Date, 1, 4) = '2012' THEN Consumption ELSE 0 END) -        SUM(CASE WHEN SUBSTR(Date, 1, 4) = '2013' THEN Consumption ELSE 0 END)) /        SUM(CASE WHEN SUBSTR(Date, 1, 4) = '2012' THEN Consumption ELSE 0 END)) AS DecreaseRate FROM yearmonth WHERE CustomerID = (     SELECT CustomerID     FROM transactions_1k     WHERE Date = '2012-08-25' AND Price = 634.8 ); 
SELECT      t.GasStationID,      SUM(t.Price * t.Amount) AS TotalRevenue FROM      transactions_1k t GROUP BY      t.GasStationID ORDER BY      TotalRevenue DESC LIMIT 1; 
SELECT      (CAST(COUNT(CASE WHEN Segment = 'Premium' THEN 1 END) AS FLOAT) / COUNT(*)) * 100 AS PremiumPercentage FROM gasstations WHERE Country = 'SVK'; 
SELECT     (SELECT SUM(t.Price * t.Amount)      FROM transactions_1k t      JOIN customers c ON t.CustomerID = c.CustomerID      WHERE c.CustomerID = 38508) AS TotalSpent,     (SELECT SUM(Consumption)      FROM yearmonth      WHERE CustomerID = 38508 AND Date = '201201') AS January2012Spent; 
SELECT p.Description, SUM(t.Amount) AS TotalAmountSold FROM products p JOIN transactions_1k t ON p.ProductID = t.ProductID GROUP BY p.Description ORDER BY TotalAmountSold DESC LIMIT 5; 
WITH CustomerSpending AS (     SELECT          t.CustomerID,         SUM(t.Price) AS TotalSpending,         c.Currency     FROM          transactions_1k t     JOIN          customers c ON t.CustomerID = c.CustomerID     GROUP BY          t.CustomerID ), TopCustomer AS (     SELECT          CustomerID,         TotalSpending,         Currency     FROM          CustomerSpending     ORDER BY          TotalSpending DESC     LIMIT 1 ) SELECT      tc.CustomerID,     tc.TotalSpending / SUM(t.Amount) AS AveragePricePerItem,     tc.Currency FROM      TopCustomer tc JOIN      transactions_1k t ON tc.CustomerID = t.CustomerID; 
